  type: String!
  metadata: EventMetadata!
}
type UserCreatedEvent implements DomainEvent {
  type: String!
  metadata: EventMetadata!
  userId: String!
  email: String!
  username: String!
  timestamp: Float!
}

# Entities
type User @key(fields: "id") {
  id: String!
  email: String!
  username: String!
  firstName: String!
  lastName: String!
  isActive: Boolean!
  createdAt: Float!
  updatedAt: Float!
}

extend union _Entity = User

# Root Types

type Query {
  GetUser(input: GetUserInput!): QueryResult!
}

type Mutation {
  CreateUser(input: CreateUserInput!): CommandResult!
}

type CommandResult {
  success: Boolean!
  aggregateId: ID
  version: Int
  error: String
}

type QueryResult {
  data: String!
  metadata: QueryMetadata!
}

type EventMetadata {
  eventId: ID!
  aggregateId: ID!
  version: Int!
  timestamp: Float!
  correlationId: String!
  causationId: String!
  actor: String!
}

type QueryMetadata {
  timestamp: Float!
  executionTime: Float!
  cached: Boolean!
}

